mapper.select:
SELECT game_area.world_id world_id, game_area.area_id area_id, game_area.world_name world_name, game_area.area_name area_name, game_area.area_type area_type, game_area.external_ip external_ip, game_area.tcp_port tcp_port, game_area.web_port web_port, game_area.inner_port inner_port, game_area.inner_ip inner_ip, game_area.pid pid, game_area.`status` `status`, game_area.follower_id follower_id, game_area.open_time open_time, game_area.update_time update_time, game_area.combine_time combine_time, game_area.server_version FROM game_area WHERE 1=1 and pid = #{pids} and area_id = #{areaId}
AND world_id = #{serverId}
ORDER BY game_area.pid ASC, game_area.world_id ASC

SELECT game_area.id id, game_area.world_id world_id, game_area.area_id area_id, game_area.world_name world_name, game_area.area_name area_name, game_area.area_type area_type, game_area.external_ip external_ip, game_area.tcp_port tcp_port, game_area.web_port web_port, game_area.inner_port inner_port, game_area.inner_ip inner_ip, game_area.pid pid, game_area.`status` `status`, game_area.follower_id follower_id, game_area.open_time open_time, game_area.update_time update_time FROM game_area game_area WHERE 1 = 1
AND game_area.world_id = #{serverId}
AND game_area.pid = #{pid}
ORDER BY game_area.pid ASC, game_area.world_id DESC

SELECT game_area.id id,
platform.name pName FROM game_area game_area left join game_platform platform on game_area.pid = platform.pid WHERE game_area.pid = #{pid}
AND area_id IN
#{item}
ORDER BY game_area.pid ASC, game_area.world_id ASC

