mapper.select:
select id,user_id userId, role_id roleId, role_name roleName,isjinyan,isfenghao,server_id serverId, operation_type operationType,msg,expire_time expireTime,create_name createName,create_date createDate from role_silence_freeze_log where 1 = 1
and role_id = #{roleId} 
and role_name = #{roleName} 
and create_date  < DATE_ADD(#{createDateEnd},INTERVAL 1 day)
and create_date  >= #{createDateStart}
order by createDate desc
